selector:before {
    border-bottom: 1px solid #EF7D00;
    border-top: 1px solid #EF7D00;
    content: "";
    height: 100%;
    left: 50%;
    position: absolute;
    top: 0;
    transition: all 500ms;
    width: 0;
    }
    
    selector:after {
    border-left: 1px solid #EF7D00;
    border-right: 1px solid #EF7D00;
    content: "";
    height: 0;
    left: 0;
    position: absolute;
    top: 50%;
    transition: all 500ms;
    width: 100%;
    }
    
    selector:hover:before {
    left: 0;
    width: 100%;
    }
    
    selector:hover:after {
    height: 100%;
    top: 0;
    }

