.noise {
    display: block;
    width: 100vw;
    height: 100vh;
    opacity: .11;
    background-image: url('http://primecontrol.online/wp-content/uploads/2023/01/noise.gif');
    overflow: hidden;
    position: fixed;
    top: 0;
    left: 0;
    pointer-events: none;
    z-index: 1;
    mix-blend-mode: plus-lighter; //opcional
}

