settings.custom_css:
html, body{
    width: 100%;
    overflow-x: hidden;
}

selector .elementor-swiper-button-prev{
    top: 110%;
    left: calc(50% - 20px);
    transform: translate(-50%, 0);
}

selector .elementor-swiper-button-next{
    top: 110%;
    right: calc(50% - 40px);
    transform: translate(-50%, 0);
}

selector .swiper-slide-prev{
    filter: blur(5px);
    transform: scale(0.5);
    transition: 0.5s;

}

selector .swiper-slide-active{
    filter: blur(5px);
    transform: scale(0.7);
    transition: 0.5s;
}


selector .swiper-slide-next{
    transform: scale(1.3);
    transition: 0.5s;
}


selector .swiper-slide-next ~ .swiper-slide{
    filter: blur(5px);
    transform: scale(0.7);
    transition: 0.5s;
}
